idx1=0;
function saveall(){
  if (idx1 < ipts.length) {
    var m=idx1;
    idx1++;
    
    aa1(ipts[m]);
    
  } else {
    alert('save ok');
  }
}
function aa1(fromObj){
  var obj1=fromObj.parentNode.nextElementSibling.getElementsByTagName('span')[0];
  if(obj1.innerHTML==''){
    setTimeout(saveall,10);
    return;
  }
  var id=fromObj.parentNode.parentNode.getAttribute('EWA_KEY');
  var u=window.location
      +'&ewa_action=extendAction0&ewa_ajax=json&a='+obj1.id+'&v='
      +obj1.innerHTML.toURL()+'&id='+id;
  console.log(u)
  $J(u,function(){
    setTimeout(saveall,10);
  });
}

Set.Bottom:
var ipts;
var idx;
function tall() {
  var o1 = $('span[id=APP_TITLE]');
  var o2 = $('span[id=APP_MAP_LABEL]');
  var o3 = $('span[id=APP_MAP_MSG]');
  
  ipts=[];  
  addToIpts(o1);
  addToIpts(o2);
  addToIpts(o3);
  
  idx = 0;
  bb();
}
function addToIpts(o){
  o.each(function(){
    ipts.push(this);
  });
}
var trans;
function aa(fromObj) {
  var obj1=fromObj.parentNode.nextElementSibling.getElementsByTagName('span')[0];
  
  if (obj1.innerHTML != '') {
    return;
  }
  if (trans == null) {
    trans = new EWA_UT_TRANS();
  }
  trans.TransToEn(fromObj.innerHTML, obj1);
}
function bb() {
  if (idx < ipts.length) {
    aa(ipts[idx]);
    idx++;
    setTimeout(bb, 123);
  } else {
    //alert('ok');
  }
}

Set.Top:
function cvtParas(){
  var s=$X('APP_URL_PARAS').value;
  var u=new EWA_UrlClass();
  u.SetUrl('a?'+s);
  var s1=[];
  for(var n in u._Paras){
    s1.push(n+'=@'+n);
  }
  $X('APP_URL_PARAS').value=s1.join('&');
}
function params(){
  var v=$X('APP_XML_NAME').value;
  if(v.indexOf('&')>0){
    var u=new EWA_UrlClass();
    u.SetUrl(v);
    var x=u.GetParameter('xmlname');
    var i=u.GetParameter('itemname');
    if(x!=null && i!=null){
      $X('APP_XML_NAME').value=x;
      $X('APP_ITEM_NAME').value=i;
    }
    
  }
}
function loadCfg(){
  var u='./?xmlname='+$X('APP_XML_NAME').value+'&itemname='+$X('APP_ITEM_NAME').value
      +'&ewa_ajax=json_ext&EWA_FRAMESET_NO=1';
  $J(u,loadCfg1);
}
function loadCfg1(data){
  var cfg=data.CFG;
  var s=[];
  s.push('<table cellpadding=1 cellspacing=1>');
  for(var i=0;i<cfg.length;i++){
    var c=cfg[i]
        var s1='<tr><td>'+c.DES+": @"+c.NAME+ '</td></tr>';
    s.push(s1);
  }
  s.push('</table>');
  $X('v1').innerHTML=s.join('');
}
function chType(){
  var obj=$X('_ewa_tr$APP_MAP_MSG');
  var o=$X('APP_TYPE');
  if(o.value=='js'){
    obj.cells[0].setAttribute('aaa',obj.cells[0].innerHTML);
    obj.cells[0].innerHTML='!!请写脚本!!';
    $X('APP_MAP_MSG').focus();
  }else if(o.value=='ref'){
    var u=location.href+'&ewa_action=ref&ewa_ajax=json';
    $J(u,$X('APP_FRAME_DETAIL_ID'),'APP_TITLE','APP_ID',true,'@APP_FRAME_DETAIL_ID');
  } else {
    var s=obj.cells[0].getAttribute('aaa');
    if(s==null || s==''){
      return;
    }
    obj.cells[0].innerHTML=obj.cells[0].getAttribute('aaa');
  }
}

Set.Bottom:
function loadIcons() {
  var u = '@EWA.CP/EWA_STYLE/skins/icons.css';
  $.get(u,
  function(v) {
    loadIcons1(v);
  });
  chType();
  var td=$X('_ewa_tr$APP_URL_PARAS').cells[0];
  td.innerHTML='<a href="javascript:cvtParas();">'+td.innerHTML+'</a>';
  
}
function loadIcons1(v) {
  var v1 = '@APP_ADD_PARA2';
  var mm = v.match(/.icon-[^:]+/ig);
  var o = $X('APP_ADD_PARA2');
  var opt = new Option('', '');
  o.options[o.options.length] = opt;
  for (var i in mm) {
    var t = mm[i].replace('.', '');
    var opt = new Option(t, t);
    o.options[o.options.length] = opt;
    if (v1 == t) {
      opt.selected = true;
    }
  }
  $(o).parent().append('&nbsp; <span style="color:blue" id=zzz></span>');
  o.onchange = function() {
    showIcon(this);
  };
  showIcon(o);
}
function showIcon(obj) {
  $X('zzz').className = 'icon ' + obj.value;
}
addEvent(window, 'load', loadIcons);
var maps;
function copyXml() {
  maps = {};
  var objs = document.getElementsByTagName('input');
  copyItems(objs);
  objs = document.getElementsByTagName('select');
  copyItems(objs);
  objs = document.getElementsByTagName('textarea');
  copyItems(objs);
  window.parent.MAPS = maps;
  alert('拷贝完成，可以切换进行复制');
}
function copyItems(objs) {
  for (var i = 0; i < objs.length; i++) {
    var o = objs[i];
    if (o.type == 'input' || o.type == 'submit') {
      continue;
    }
    if (o.id == 'APP_PID' || o.id == 'APP_LVL' || o.id == 'APP_ORD') {
      continue;
    }
    maps[o.id] = o.value;
  }
}
function parseXml() {
  var maps = window.parent.MAPS;
  if (maps == null) {
    alert('请先复制配置');
    return;
  }

  for (var n in maps) {
    var v = maps[n];
    try {
      $X(n).value = v;
    } catch(e) {
      alert(n + ',' + v + ',' + e);
    }
  }
  alert('复制完毕');
}

function link(id){
  var u;
  if('@en'==1){
    u="./?xmlname=@xmlname&itemname=_ewa_app_cfg.frame.Des&app_id="+id;
  }else{
    u="./?xmlname=@xmlname&itemname=_ewa_app_cfg.frame.modify&ewa_redraw=1&app_id="+id;
  }
  window.parent.frames[1].location=u;
  
}

