UPDATE _EWA_APP_CFG SET APP_TITLE = @APP_TITLE, APP_XML_NAME = @APP_XML_NAME, APP_ITEM_NAME = @APP_ITEM_NAME, APP_URL_PARAS = @APP_URL_PARAS, APP_MAP_LABEL = @APP_MAP_LABEL, APP_MAP_MSG = @APP_MAP_MSG, APP_SEARCH_NAME = @APP_SEARCH_NAME, APP_BUT_TITLE = @APP_BUT_TITLE, APP_TYPE = @APP_TYPE, APP_VIEW = @APP_VIEW, APP_URL = @APP_URL, APP_SKIP = @APP_SKIP, APP_PID = @APP_PID, APP_FRAME_DETAIL_ID = @APP_FRAME_DETAIL_ID, APP_FRAME_MODIFY_ID = @APP_FRAME_MODIFY_ID, APP_FRAME_ADD_ID = @APP_FRAME_ADD_ID, APP_LVL = @APP_LVL, APP_ORD = @APP_ORD, APP_ADD_PARA0 = @APP_ADD_PARA0, APP_ADD_PARA1 = @APP_ADD_PARA1, APP_ADD_PARA2 = @APP_ADD_PARA2, APP_TITLE_ENUS = @APP_TITLE_ENUS, APP_MAP_LABEL_ENUS = @APP_MAP_LABEL_ENUS, APP_MAP_MSG_ENUS = @APP_MAP_MSG_ENUS, APP_BUT_TITLE_ENUS = @APP_BUT_TITLE_ENUS, APP_LOGIN=@APP_LOGIN, APP_JS_LOADED=@APP_JS_LOADED, APP_SCROLL_LOAD_MORE=@APP_SCROLL_LOAD_MORE
WHERE APP_ID=@APP_ID;
EXEC PR_EWA_APP_CFG_LVL @APP_ID;
