(function() {
    var ewa = EWA.F.FOS["@SYS_FRAME_UNID"];
    //提交后台数据时执行的检查或附加数据处理
    ewa.DoPostBefore = function() {
        /*
		//附加数据处理
		// ewa.PostAddData = {add1:'a',add2:2};

        // 附加检查合法性
    	if(getObj('#aaa').val()==''){
    		return false; //检查不通过
    	}else{
    		return true; //检查通过
    	}
	    */
        return true;
    };

    function getObj(exp) {
        return $('#f_@sys_frame_unid').find(exp);
    }

    function init() {
        getObj('#CONF_TAG').on('change', function() {
            var u1 = new EWA_UrlClass(EWA.F.FOS["@SYS_FRAME_UNID"].Url);
            u1.AddParameter("ewa_action", "SAct0");
            u1.AddParameter("ewa_ajax", "json");
            u1.AddParameter("CONF_TAG", this.value);
            var u = u1.GetUrl();
            $J(u, getObj('#PARA_NAME')[0], 'PARA_NAME', 'PARA_NAME', true, '@PARA_NAME');
        });
        if ("@CONF_TAG") {
            getObj('#CONF_TAG').trigger('change');
        }
    }
    init();
})();

//EWA_CONF.LF.M
(function() {
    var ewa = EWA.F.FOS['@SYS_FRAME_UNID'];
    ewa.ReloadAfter = function(httpReferer) {

    };

    ewa.AddPreRow(function(frameUnid, tr, key, newTr, evt) {
        var cnt_td = newTr.cells[0]; //新行的td

        var u1 = new EWA_UrlClass(EWA.F.FOS["@SYS_FRAME_UNID"].Url);
        u1.AddParameter("ITEMNAME", "EWA_CONF_PARA.LF.M");
        u1.AddParameter("CONF_TAG", key);

        // 通过ajax加载内容
        var ajax_url = u1.GetUrl();
        $Install(ajax_url, cnt_td, function() {
            console.log('install ok');  
        });

        
    });
    ewa.ext_AddNew = function() {
        var u1 = new EWA_UrlClass(ewa.Url);
        u1.RemoveEwa();
        u1.AddParameter("EWA_MTYPE", "N");
        var paras = u1.GetParas();
        EWA.UI.Dialog.OpenReloadClose('@SYS_FRAME_UNID', '@xmlName', 'EWA_CONF.F.NM', false, paras);
    };

    function getObj(exp) {
        return exp ? $('#EWA_LF_@sys_frame_unid').find(exp) : $('#EWA_LF_@sys_frame_unid');
    }

    // 执行刷新后事件
    function init() {
        ewa.ReloadAfter();
    }
    init();
})();

