//EWA_CONF_DICT.LF.M
(function(){
	var ewa = EWA.F.FOS['@SYS_FRAME_UNID'];
	ewa.ReloadAfter = function(httpReferer){
	 
	};

	ewa.ext_AddNew = function(){
		var u1 = new EWA_UrlClass(ewa.Url);
		u1.RemoveEwa();
		u1.AddParameter("EWA_MTYPE","N");
		var paras = u1.GetParas();
		EWA.UI.Dialog.OpenReloadClose('@SYS_FRAME_UNID','@xmlName', 'EWA_CONF_DICT.F.NM', false, paras);
	};

	function getObj(exp){
		return exp?$('#EWA_LF_@sys_frame_unid').find(exp):$('#EWA_LF_@sys_frame_unid');
	}
	
	// 执行刷新后事件
	function init(){
		ewa.ReloadAfter ();
	}
	init();
})();

//EWA_CONF.LF.M
(function() {
    var ewa = EWA.F.FOS['@SYS_FRAME_UNID'];
    ewa.IsNotMDownAutoChecked = true;
    ewa.MDownEvent = function(tr, evt) {
        let tag = $(tr).attr('ewa_key');
        let conf = ewa._confs[tag];
        let obj = $(tr).next();
        obj.toggle();
    };

    function getObj(exp) {
        return exp ? $('#EWA_LF_@sys_frame_unid').find(exp) : $('#EWA_LF_@sys_frame_unid');
    }

    function loadActionJson() {
        var u1 = new EWA_UrlClass(EWA.F.FOS["@SYS_FRAME_UNID"].Url);
        u1.AddParameter("ewa_action", "SACT0");
        u1.AddParameter("ewa_ajax", "json_all");
        var u = u1.GetUrl();
        $J(u, function(rst) {
            ewacConfDesign('@SYS_FRAME_UNID', rst);
        });
    }


    // 执行刷新后事件
    function init() {
        getObj('input[type=checkbox]').prop('checked', true);
        loadActionJson();
    }
    init();
})();

