        u1.AddParameter("mod_ver_id", mod_ver_id);
        u1.AddParameter("mod_code", mod_code);
        u1.AddParameter("mod_ver", mod_ver);

         

        u1.AddParameter("ewa_conn", "@mod_ewa_conn");

        let url = u1.GetUrl();
        console.log(url);

        ewa.ok = false;
        $Tip('创建中，请等待...', () => {
            return ewa.ok;
        });

        $J1(url, (rst) => {
            ewa.ok = true;
            if (!rst.RST) {
                alert(rst.ERR);
            } else {
                alert('创建成功！');
            }
        });

    };
