(function() {
    var ewa = EWA.F.FOS["@SYS_FRAME_UNID"];
    //提交后台数据时执行的检查或附加数据处理
    ewa.DoPostBefore = function() {
        let cnt = getObj('#parameters').val().trim();
        if (!cnt) {
            $Tip('请先粘贴要下载的参数')
            return;
        }
        try {
            let obj = JSON.parse(cnt);
        } catch (e) {
            $Tip('参数格式错误：' + e);
            return;
        }
        
        
        let u = "@EWA.CP/EWA_DEFINE/cgi-bin/group/?type=publish_module_download";
        
        let data = {data: cnt}
        
        $JP(u, data, function(rst){
            console.log(rst);
        });
        
        return  ;
    };

    function getObj(exp) {
        return $('#f_@sys_frame_unid').find(exp);
    }

    function init() {}
    init();
})();

