/**
 * Frame 提交前进行检查，返回true，则可以提交数据
 * 同时可以手动增加提交的参数
 **/
EWA.F.FOS["@sys_frame_unid"].DoPostBefore = function() {
    var os = window.frames[0].document.getElementsByTagName('input');
    var s = [];
    var r = /\;/ig;
    let tables = [];
    let tableTypes = [];
    let schemas = [];
    let cnns = [];
    let dbTypes = [];

    for (var i = 0; i < os.length; i++) {
        var o = os[i];
        if (o.type == 'checkbox' && o.checked) {
            console.log(o.value)
            //   TABLE  ;EWA;ewa_cfg;MYSQL;ewa
            let vals = o.value.split(';')
            tableTypes.push(vals[0].trim());
            schemas.push(vals[1].trim());
            tables.push(vals[2].trim());
            dbTypes.push(vals[3].trim());
            cnns.push(vals[4].trim());
        }
    }
    if (cnns.length == 0) {
        $Tip('请选择');
        return;
    }

    let data = {
        tables: tables.join('`'),
        tableTypes: tableTypes.join('`'),
        schemas: schemas.join('`'),
        dbTypes: dbTypes.join('`'),
        cnns: cnns.join('`')
    };
    console.log(data)
	this.PostAddData = data;
    
    return true;
}

(function(){				
	var ewa = EWA.F.FOS["@SYS_FRAME_UNID"];
	//提交后台数据时执行的检查或附加数据处理
    ewa.DoPostBefore = function() {
        let u1 = new EWA_UrlClass(this.Url);
        u1.RemoveEwa();
        if(!getObj("#import_data_conn").val()){
            $Tip('请输入 导入数据的连接名称 ');
            return;
        }
        u1.AddParameter("import_data_conn", getObj("#import_data_conn").val());
        if(!getObj("#replace_meta_databaseName").val()){
            $Tip('请输入 替换元数据库名称');
            return;
        }
        u1.AddParameter("replace_meta_databaseName", getObj("#replace_meta_databaseName").val());
        if(!getObj("#replace_work_databaseName").val()){
            $Tip('请输入 替换工作据库名称');
            return;
        }
        u1.AddParameter("replace_work_databaseName", getObj("#replace_work_databaseName").val());
        
        let paras = u1.GetParas();
        let u = "@EWA.CP/EWA_DEFINE/cgi-bin/group/?type=publish_module_import&"+paras;
        
        
        $J1(u, function(rst){
            console.log(rst);
        });
        
        return false ;
    };
	function getObj(exp){
		if(exp){
			return $('#f_@sys_frame_unid').find(exp);
		} else {
			return $('#f_@sys_frame_unid');
		}
	}
	function init(){
		if(window.EWA_App){
			// app
		} 
		// 在对话框里限制最高高度，默认400px
		if ("@EWA_PARENT_FRAME" && !window.EWA_App) { 
			// getObj().parent().addClass("ewa-in-dialog");
        }
	}
	init();
})();

