(function() {
    var ewa = EWA.F.FOS["@SYS_FRAME_UNID"];
    //提交后台数据时执行的检查或附加数据处理
    ewa.DoPostBefore = function() {
        let cnt = getObj('#parameters').val().trim();
        if (!cnt) {
            $Tip('请先粘贴要下载的参数')
            return;
        }
        try {
            let obj = JSON.parse(cnt);
        } catch (e) {
            $Tip('参数格式错误：' + e);
            return;
        }
        this.download_complete = false;

        $Tip('下载中，请稍后...', () => {
            return ewa.download_complete
        });


        let u = "@EWA.CP/EWA_DEFINE/cgi-bin/group/?type=publish_module_download";

        let data = {
            data: cnt
        }

        $JP(u, data, (rst) => {
            ewa.download_complete = true;
            if (!rst.RST) {
                alert(rst.ERR);
                return;
            }
            EWA.OW.Load();
            EWA.OW.PWin.EWA.F.FOS["@EWA_PARENT_FRAME"].Reload();
            EWA.OW.Close();
        });

        return;
    };

    function getObj(exp) {
        return $('#f_@sys_frame_unid').find(exp);
    }

    function init() {}
    init();
})();

