(function() {
    var ewa = EWA.F.FOS["@SYS_FRAME_UNID"];
    //提交后台数据时执行的检查或附加数据处理
    ewa.DoPostBefore = function() {
        if (ewa.stop) {
            return;
        }

        let u1 = new EWA_UrlClass(this.Url);
        u1.RemoveEwa();
        if (!getObj("#import_data_conn").val()) {
            $Tip('请输入 导入数据的连接名称 ');
            return;
        }
        u1.AddParameter("import_data_conn", getObj("#import_data_conn").val());
        if (!getObj("#replace_meta_databaseName").val()) {
            $Tip('请输入 替换元数据库名称');
            return;
        }
        u1.AddParameter("replace_meta_databaseName", getObj("#replace_meta_databaseName").val());
        if (!getObj("#replace_work_databaseName").val()) {
            $Tip('请输入 替换工作据库名称');
            return;
        }
        u1.AddParameter("replace_work_databaseName", getObj("#replace_work_databaseName").val());

        let import_table = getObj('[name=import_table]:checked').val();
        if (!import_table) {
            $Tip('请选择是否导入表/视图结构');
            return;
        }
        u1.AddParameter("import_table", import_table);

        let import_data = getObj('[name=import_data]:checked').val();
        if (!import_data) {
            $Tip('请选择是否导入表数据');
            return;
        }
        u1.AddParameter("import_data", import_data);

        let import_cfg = getObj('[name=import_cfg]:checked').val();
        if (!import_cfg) {
            $Tip('请选择是否导入配置项');
            return;
        }
        u1.AddParameter("import_cfg", import_cfg);

        getObj('#butSubmit').hide();
        ewa.stop = true;
        let paras = u1.GetParas();
        let u = "@EWA.CP/EWA_DEFINE/cgi-bin/group/?type=publish_module_import&" + paras;

        $Tip('导入中...', () => {
            return !ewa.stop;
        })
        $J1(u, function(rst) {
            console.log(rst);
            
            ewa.stop = false;
            if (!rst.RST) {
                alert(rst.ERR);
                return;
            }
            if (rst.importDataOrTableError) {
                let pre = $("<pre style='background:#fff;margin:0;oveflow:auto;height:100vh;width:100%'></pre>");
                pre.text(rst.importDataOrTableError);
                getObj("").html("").append(pre);
            }
            
            EWA.OW.Load();
            if (EWA.OW.Dia) {
                EWA.OW.Dia.CloseWindow();
            }
        });

        return false;
    };

    function getObj(exp) {
        if (exp) {
            return $('#f_@sys_frame_unid').find(exp);
        } else {
            return $('#f_@sys_frame_unid');
        }
    }

    function init() {
        if (window.EWA_App) {
            // app
        }
        // 在对话框里限制最高高度，默认400px
        if ("@EWA_PARENT_FRAME" && !window.EWA_App) {
            // getObj().parent().addClass("ewa-in-dialog");
        }
    }
    init();
})();

