(function() {
    var ewa = EWA.F.FOS["@SYS_FRAME_UNID"];
    let mod_ver_id = "@mod_ver_id";
    let mod_code = "@mod_code";
    let mod_ver = "@mod_ver";
    let ewacnn = "@mod_ewa_conn";
    ewa.publishPackage = function() {
        if (ewa.startMakePackage) {
            return;
        }
        ewa.startMakePackage = true;

        let u1 = new EWA_UrlClass("@EWA.CP/EWA_DEFINE/cgi-bin/group/?type=publish_module");
        u1.AddParameter("mod_ver_id", mod_ver_id);
        u1.AddParameter("mod_code", mod_code);
        u1.AddParameter("mod_ver", mod_ver);
        u1.AddParameter("ewa_conn", ewacnn);

        let url = u1.GetUrl();

        $Tip('发布中，请等待...', () => {
            return ewa.startMakePackage === false;
        });

        $J1(url, (rst) => {
            ewa.startMakePackage = false;
            if (!rst.RST) {
                alert(rst.ERR);
            } else {
                $Tip('发布完成！');
                let u1 = new EWA_UrlClass(ewa.Url);
                u1.AddParameter("itemname", 'ewa_mod_ver.F.info');
                $Install(u1.GetUrl(), 'info', function() {});
            }
        });
    };
    ewa.makePackage = function() {

        if (ewa.startMakePackage) {
            return;
        }
        ewa.startMakePackage = true;

        let u1 = new EWA_UrlClass("@EWA.CP/EWA_DEFINE/cgi-bin/group/?type=export_module");
        u1.AddParameter("mod_ver_id", mod_ver_id);
        u1.AddParameter("mod_code", mod_code);
        u1.AddParameter("mod_ver", mod_ver);
        u1.AddParameter("ewa_conn", ewacnn);

        let url = u1.GetUrl();
        console.log(url);

        $Tip('创建中，请等待...', () => {
            return ewa.startMakePackage === false;
        });

        $J1(url, (rst) => {
            ewa.startMakePackage = false;
            if (!rst.RST) {
                alert(rst.ERR);
            } else {
                $Tip('创建成功！');

                let u1 = new EWA_UrlClass(ewa.Url);
                u1.AddParameter("itemname", 'ewa_mod_ver.F.info');
                $Install(u1.GetUrl(), 'info', function() {});
            }
        });
    };
    ewa.DoPostBefore = function() {
        return true;
    };

    function getObj(exp) {
        return $('#f_@sys_frame_unid').find(exp);
    }

    function init() {
        getObj('.IX_SPAN').removeAttr('title');

        let u1 = new EWA_UrlClass(ewa.Url);
        u1.AddParameter("itemname", 'ewa_mod_ver.F.info');
        $Install(u1.GetUrl(), 'info', function() {});

        u1.AddParameter("itemname", 'ewa_mod_cfgs.LF.M');
        $Install(u1.GetUrl(), 'cfgs', function() {});

        // 数据库实体
        u1.AddParameter("itemname", 'ewa_mod_ddl.LF.M');
        $Install(u1.GetUrl(), 'tables', function() {});
    }
    init();
})();

