function openFrame(id) {
    let ewa = EWA.F.FOS['@SYS_FRAME_UNID'];
    let u1 = new EWA_UrlClass(ewa.Url);
    u1.AddParameter("ITEMNAME", "ewa_mod_ver.F.frames");
    u1.AddParameter("mod_ver_id", id);
    u1.RemoveParameter("EWA_AJAX");
    let u = u1.GetUrl();
    if (top.AddTab) {
        top.AddTab(u)
    } else {
        window.open(u);
    }
}

 
