function clearSelect() {
    var o = window.frames[0].frames[1].document.getElementsByTagName('input');
    for (var i = 0; i < o.length; i++) {
        var o1 = o[i];
        if (o1.checked) {
            o1.checked = false;
        }
    }
}

function addTables(){
    EWA.F.FOS["@sys_frame_unid"].DoPost();
}
/**
 * Frame 提交前进行检查，返回true，则可以提交数据
 * 同时可以手动增加提交的参数
 **/
EWA.F.FOS["@sys_frame_unid"].DoPostBefore = function() {
    var os = window.frames[0].frames[1].document.getElementsByTagName('input');
    var xmlnames = [];
    var itemnames = [];
    var descriptions = [];
    for (var i = 0; i < os.length; i++) {
        var o = os[i];
        if (o.type == 'checkbox' && o.checked) {
            var tr = o.parentNode.parentNode.parentNode.parentNode;
            xmlnames.push(window.frames[0].frames[1].VIEW_XMLNAME);
            itemnames.push($(tr).find('.ewa-col-ssss').text().trim());
            descriptions.push($(tr).find('.ewa-col-getdescription').text().trim());
        }
    }
    if (xmlnames.length == 0) {
        $Tip('请选择');
        return false;
    }

    var add_data = {};
    add_data["xmlnames"] = xmlnames.join('`');
    add_data["itemnames"] = itemnames.join('`');
    add_data["descriptions"] = descriptions.join('`');
    this.PostAddData = add_data;

    return true;
}

function clearSelect() {
    var o = window.frames[0].document.getElementsByTagName('input');
    for (var i = 0; i < o.length; i++) {
        var o1 = o[i];
        if (o1.checked) {
            o1.checked = false;
        }
    }
}

function addTables() {
    EWA.F.FOS["@sys_frame_unid"].DoPost();
}
/**
 * Frame 提交前进行检查，返回true，则可以提交数据
 * 同时可以手动增加提交的参数
 **/
EWA.F.FOS["@sys_frame_unid"].DoPostBefore = function() {
    var os = window.frames[0].document.getElementsByTagName('input');
    var s = [];
    var r = /\\;/ig;
    let tables = [];
    let tableTypes = [];
    let schemas = [];
    let cnns = [];
    let dbTypes = [];

    for (var i = 0; i < os.length; i++) {
        var o = os[i];
        if (o.type == 'checkbox' && o.checked) {
            console.log(o.value)
            //   TABLE  ;EWA;ewa_cfg;MYSQL;ewa
            let vals = o.value.split(';')
            tableTypes.push(vals[0].trim());
            schemas.push(vals[1].trim());
            tables.push(vals[2].trim());
            dbTypes.push(vals[3].trim());
            cnns.push(vals[4].trim());
        }
    }
    if (cnns.length == 0) {
        $Tip('请选择');
        return;
    }

    let data = {
        tables: tables.join('`'),
        tableTypes: tableTypes.join('`'),
        schemas: schemas.join('`'),
        dbTypes: dbTypes.join('`'),
        cnns: cnns.join('`')
    };
    console.log(data)
	this.PostAddData = data;
    
    return true;
}

