Set.Bottom:
(function() {
    let methods = {
        GET: {
            endFix: ".LF.M",
            nameFix: "获取列表",
            nameFixEn: "Get the list of "
        },
        DELETE: {
            endFix: ".LF.M",
            nameFix: "逻辑删除",
            nameFixEn: "Logic delete "
        },
        PATCH: {
            endFix: ".LF.M",
            nameFix: "恢复逻辑删除",
            nameFixEn: "Recover deleted "
        },
        POST: {
            endFix: ".F.NM",
            nameFix: "新增",
            nameFixEn: "Add the "
        },
        PUT: {
            endFix: ".F.NM",
            nameFix: "修改",
            nameFixEn: "Modify the "
        }
    };
    var ewa = EWA.F.FOS["@SYS_FRAME_UNID"];
    //提交后台数据时执行的检查或附加数据处理
    ewa.DoPostBefore = function() {
        getObj("#butOk").prop('disabled',true);
        let xmlname = getObj('#xmlname1').val();
        let itemname = getObj('#itemname1').val().split('\\\\.')[0];
        let name = getObj('#name').val();
        let nameEn = getObj('#name_en').val();

        for (let method in methods) {
            let cfg = methods[method];
            let u = new EWA_UrlClass(EWA.F.FOS["@SYS_FRAME_UNID"].Url);
            u.AddParameter("itemname", "ewa_restful.F.NM");
            u.AddParameter("ewa_ajax", "json");
            u.AddParameter("ewa_action", "OnPagePost");
            u.AddParameter("ewa_mtype", "N");

            u.AddParameter("rs_method", method);
            u.AddParameter("rs_name", cfg.nameFix + name);
            u.AddParameter("rs_name_en", cfg.nameFixEn + nameEn);
            u.AddParameter("rs_xmlname", xmlname);
            u.AddParameter("rs_itemname", itemname + cfg.endFix);
            u.AddParameter("rs_parameters", "");
            $J(u.GetUrl(), function(rst) {
                cfg.ok = 1;
                checkAllDone();
            });
        }
        return false;
    };

    function checkAllDone() {
        for (let method in methods) {
            let cfg = methods[method];
            if (!cfg.ok) {
                return;
            }
        }
        $Tip('All down');
        if ("@EWA_PARENT_FRAME") {
            EWA.OW.Load();
            EWA.OW.PWin.EWA.F.FOS["@EWA_PARENT_FRAME"].Reload();
            EWA.OW.Close();
        }
    }

    function getObj(exp) {
        return $('#f_@sys_frame_unid').find(exp);
    }

    function init() {}
    init();
})();

