(function() {
    var ewa = EWA.F.FOS["@SYS_FRAME_UNID"];
    //提交后台数据时执行的检查或附加数据处理
    ewa.DoPostBefore = function() {
        let ppath = getObj("#ppath").text();
        //附加数据处理
        ewa.PostAddData = {
            "cat_path_full": (ppath ? ppath + '/' : "") + getObj("#cat_path").val()
        };
        let paths = ewa.PostAddData.cat_path_full.split('/');
        for (let i = 0; i < paths.length; i++) {
            ewa.PostAddData["p" + i] = paths[i];
        }

        return true;
    };

    function getObj(exp) {
        return $('#f_@sys_frame_unid').find(exp);
    }


    function loadActionJson_SAct0() {
        var u1 = new EWA_UrlClass(EWA.F.FOS["@SYS_FRAME_UNID"].Url);
        u1.AddParameter("ewa_action", "SAct0");
        u1.AddParameter("ewa_ajax", "json");
        u1.AddParameter("cat_uid", "@cat_uid");

        var u = u1.GetUrl();
        $J(u, function(rst) {
            if (rst.length == 0) {
                return;
            }
            let d = rst[0];
            let paths = [];
            for (let i = 1; i < 10; i++) {
                let key = 'path_' + i;
                if (d[key] != null) {
                    paths.push(d[key]);
                }
            }
            let ss = [];
            for (let i = paths.length - 1; i >= 0; i--) {
                ss.push(paths[i]);
            }
            getObj("#ppath").text(ss.join("/"));
        });
    }

    function init() {
        loadActionJson_SAct0()
    }
    init();
})();

