var app_ds='@APP_WF_DS';
var app_tb='@APP_WF_TABLE';

function show_tables(key){
  if(key==''){
    return;
  }
	var u = './?xmlname=ewa|ewa.xml&itemname=select_create_sql&EWA_ACTION=tables&EWA_AJAX=json&key='+key;
	var ajax=new EWA_AjaxClass();
	ajax.Get(u,function(){
		if(ajax.IsRunEnd()){
			var rst=ajax.GetRst();
			eval('tbs='+rst);
			show_tables1();
		}
	}); 
}
var ffs;
function show_tables1(){
	var obj=$X('APP_WF_TABLE');
	obj.options.length=0;
	obj.options[0]=new Option('','');
	obj.options[0].title='';

    for(var i=2;i<tbs.length;i++){
      var t=tbs[i]; 
      var keys=t.Key.split(';');
      obj.options[obj.options.length]=new Option(t.Name,t.Name);
      var opt=obj.options[obj.options.length-1];
      opt.title=t.Key
      if(opt.value == app_tb){
        opt.selected=true; 
      }
	}
    show_fields(obj);
  
}
function show_fields(obj){
    var key=obj.options[obj.selectedIndex].title;
  
	if(key==''){
		var objs=[];
		objs.push($X('APP_WF_FIELD'));
        objs.push($X('APP_WF_PKS'));
		for(var i=0; i < objs.length; i++){
			objs[i].options.length = 0;
		}
		return;
	}
	
	var keys=key.split(';');
	var u='./?xmlname=ewa%7Cewa.xml&EWA_FRAMESET_NO=1&itemname=fields&cfg='+keys[1]
		+ '&tablename=' + keys[3] + '&ewa_ajax=json'; 
	var ajax=new EWA_AjaxClass();
	ajax.Get(u,function(){
		if(ajax.IsRunEnd()){
			var rst=ajax.GetRst();
			eval('ffs='+rst);
			show_fields1();
		}
	}); 
}
function show_fields1(){
	var objs=[];
	objs.push($X('APP_WF_FIELD'));
	objs.push($X('APP_WF_PKS'));
	
	for(var i=0; i < objs.length; i++){
		objs[i].options.length = 0;
		objs[i].options[0] = new Option('','');
	}
	
	for(var i=0; i<ffs.length; i++){
		var t = ffs[i];
		for(var k = 0; k < objs.length; k ++){ 
			objs[k].options[objs[k].options.length] = 
				new Option(t.name + '(' + t.description+')', t.name);
		}
	}
	
    objs[0].value='@APP_WF_FIELD';
    objs[1].value='@APP_WF_PKS';
}

