function loadDatasource(){
  var u='./?XMLNAME=|ewa|ewa.xml&ITEMNAME=select_create_sql&a=0&datasource=DATABASE;&ewa_ajax=json&ewa_action=datasource';
  
  $J(u,$X('APP_WF_DS'),'Name','Key',true,app_ds);
  $X('APP_WF_DS').onchange=function(){
    show_tables(this.value);
  }
    
  $X('APP_WF_TABLE').onchange=function(){
    show_fields(this);
  }
}
loadDatasource();

