var lst='@WF_UNIT_ADM_LST';
var adm="@WF_UNIT_ADM";
var map;
function loadOps(){
  var t=$X('WF_UNIT_ADM');
  if(t.value ==adm){
    map={};
    var ids=lst.split(',');
    for(var i=0;i<ids.length;i++){
      map[ids[i]]=true;
    
    }
  }else{
    map={};
  }
  var u=window.location+'&EWA_AJAX=JSON&EWA_ACTION=AddAction1&T='+t.value;
  $J(u,loadOps1);

}
function loadOps1(datas){
  var o=$X('WF_UNIT_ADM_LST');
  o.innerHTML='';
  var s=[];
  
  for(var i=0;i<datas.length;i++){
    var d=datas[i];
    var id=WF_UNIT_ADM_LST+i;
    var chk='';
    if(map[d.ADM_ID]){
      chk='checked';
    }
    var s1='<input type=checkbox name="'+WF_UNIT_ADM_LST
        +'" id="'+id+'" value="'+d.ADM_ID+'" '+chk+'>'+d.ADM_NAME;
    
   s.push(s1); 
  }
  o.innerHTML=s.join('<br>');
  o.style.overflow='auto';
}

