var lst = '@WF_UNIT_ADM_LST';
var adm = "@WF_UNIT_ADM";
var map;
function loadOps() {
  var t = $X('WF_UNIT_ADM');
  if (t.value == adm) {
    map = {};
    var ids = lst.split(',');
    for (var i = 0; i < ids.length; i++) {
      map[ids[i]] = true;
    }
  } else {
    map = {};
  }
  var u = window.location + '&EWA_AJAX=JSON&EWA_ACTION=AddAction1&T=' + t.value;
  $J(u, loadOps1);

}
function loadOps1(datas) {
  split_map = {};
  split_map.ALL = [];
  var o = $X('WF_UNIT_ADM_LST');
  o.innerHTML = '';
  var s = [];
  var ss = [];
  var ss2 = [];
  var css = "font-size:16px;color:#08c;cursor:pointer;margin-right:3px;"
  for (var i = 0; i < datas.length; i++) {
    var d = datas[i];
    var lid = d.ADM_LID;
    var lids = lid.split('.');
    var lid1 = lids[lids.length - 1];
    var code = lid1.length > 0 ? lid1.substring(0, 1).toUpperCase() : '';
    if (!split_map[code]) {
      split_map[code] = [];
    }

    var id = 'WF_UNIT_ADM_LST' + i;
    var chk = '';
    if (map[d.ADM_ID]) {
      chk = 'checked';
      var s1 = '<div class="a"><input type=checkbox name="WF_UNIT_ADM_LST" id="' + id + '" value="' + d.ADM_ID + '" ' + chk + '>';
      s1+='<label  for="'+id+'">[' + d.ADM_ID + '] ' + d.ADM_NAME+"</label></div>";
      s.push(s1);
    } else {
      var s1 = '<div class="a" ><input type=checkbox name="WF_UNIT_ADM_LST " id="' + id + '" value="' + d.ADM_ID + '">';
      s1+='<label for="'+id+'">[' + d.ADM_ID + '] ' + d.ADM_NAME+"</label></div>";
      ss.push(s1);
    }
    split_map[code].push(id);
  }
  ss2.push("<span style='" + css + "' onclick='show_filter(this)'>ALL</span>");
  for (var i = 'A'.charCodeAt(); i < 'Z'.charCodeAt(); i++) {
    var code=String.fromCharCode(i);
    if(split_map[code]){
        ss2.push("<span style='" + css + "' onclick='show_filter(this)'>" + code + "</span>");
    }
  }
   
  $X('map').innerHTML = ss2.join('');
  o.innerHTML = s.join('') + '<hr>' + ss.join('');
  o.style.overflow = 'auto';
}
var last_code=null;
function show_filter(obj){
    if(last_code){
        last_code.style.color='#08c';
    }
    last_code=obj;
    obj.style.color='red';
    
    var v=obj.innerHTML;
    if(v=='ALL'){
        $('.a').show();
        return;
    } 
    $('.a').hide();
    var showmap={};
    for(var n in split_map[v]){
        var id=split_map[v][n];
        showmap[id]=1;
    }
    $('#WF_UNIT_ADM_LST input').each(function(){
        var id=this.id;
        if(this.checked|| showmap[id]){
            $(this).parent().show();
        }
    });
}

