function chv(obj){
  var tr=obj.parentNode.parentNode.parentNode;
  var key=tr.getAttribute('EWA_KEY');
  var keys=key.split(',');
  var paras=[];
  paras[0]={Name:'WF_ID', Value: keys[0]};
  paras[1]={Name:'WF_UNIT_ID', Value: keys[1]};
  paras[2]={Name:'APP_FRAME_XMLNAME', Value: keys[2]};
  paras[3]={Name:'APP_FRAME_ITEMNAME', Value: keys[3]};
  
  var chks=obj.parentNode.getElementsByTagName('input');
  var ss=[];
  for(var i=0;i<chks.length;i++){
    var chk=chks[i];
    if(chk.checked){
      ss.push(chk.title);
    }
  }
  paras[4]={Name:'XITMES', Value: ss.join(',')};
  
  EWA.F.FOS['@SYS_FRAME_UNID'].DoAction(this, 'OnFrameDelete', '', '' , paras,"AAAA=1");
  //alert(key);
}
function showAdms(){
  EWA.F.FOS["@SYS_FRAME_UNID"].ReloadAfter=showAdms;
  
  var u=window.location+'&ewa_action=extendAction0&ewa_ajax=json&x=@APP_FRAME_XMLNAME'
      +'&I=@APP_FRAME_ITEMNAME';
  $J(u,showAdms1);
}

function showAdms1(datas){
  var map=[];
  for(var i=0;i<datas.length;i++){
    var d=datas[i];
    if(map[d.getName]==null){
      map[d.getName]={};
    }
    map[d.getName]=d;
    var id='___tmp__'+Math.random();
    
    d.HTML='<input title="'+d.getName
      +'" onclick="chv(this)" id="'
      + id +'" type=checkbox [CHK]><label for="'
      + id +'">' + d.getDes + '</label>';
  }
  
  var tb=$X('EWA_LF_@sys_frame_unid');
  
  var idx=7
  for(var i=1;i<tb.rows.length;i++){
    var v=GetInnerText(tb.rows[i].cells[idx]);
     
    
    tb.rows[i].cells[idx].innerHTML=showAdms2(map,datas,v).replace(/tmp/ig,i);
  }

}
function showAdms2(map,datas,v){
  var s=[];
  var v1=','+v+',';
  for(var i=0;i<datas.length;i++){
    var d=datas[i];
    if(d.getName=='EWA_WF_UOK' || d.getName=='EWA_WF_UMSG' || d.getType=='button'){
      continue;
    }
    var h=d.HTML;
    if(v1.indexOf(','+d.getName+',')>=0){
      h=h.replace('[CHK]', 'checked');
    }
    s.push(h);
  }
  return '<nobr>'+s.join(' ')+'</nobr>';
}
addEvent(window,'load',showAdms);

