function showAdms(){
  EWA.F.FOS["@SYS_FRAME_UNID"].ReloadAfter=showAdms;
  var adms_split=[-123];
  var pos_split=[-123];
  var dep_split=[-123];
  var tb=$X('EWA_LF_@sys_frame_unid');

  var idx=7
  for(var i=1;i<tb.rows.length;i++){
    var v=GetInnerText(tb.rows[i].cells[idx]);
    if(v.trim()==''){
      continue;
    }
    var tag=tb.rows[i].cells[idx-2].getElementsByTagName('span')[1].title;
    if(tag=='WF_ADM_DEPT'){
      dep_split.push(v);
    }else if(tag=='WF_ADM_POST'){
      pos_split.push(v);
    }else if(tag=='WF_ADM_ADM'){
      adms_split.push(v);
    }
  }

  var u=window.location+'&ewa_action=extendAction1&ewa_ajax=json&adms_split='
      +adms_split.join(',')+'&pos_split='+pos_split.join(',')
      +'&dep_split='+dep_split.join(',');
  $J(u,showAdms1);
}

function showAdms1(datas){
  var map=[];
  for(var i=0;i<datas.length;i++){
    var d=datas[i];
    if(map[d.TAG]==null){
      map[d.TAG]={};
    }
    map[d.TAG][d.ADM_ID]=d.ADM_NAME;
  }

  var tb=$X('EWA_LF_@sys_frame_unid');

  var idx=7
  for(var i=1;i<tb.rows.length;i++){
    var v=GetInnerText(tb.rows[i].cells[idx]);
    if(v.trim()==''){
      continue;
    }
    var tag=tb.rows[i].cells[idx-2].getElementsByTagName('span')[1].title;
    if(!map[tag]){
      //alert(tag);
      continue;
    }
    var o=map[tag];
    var vs=v.split(',');
    var s=[];
    for(var k=0;k<vs.length;k++){
      var id=vs[k];
      s.push(o[id]);
    }
    tb.rows[i].cells[idx].innerHTML=s.join(', ');
  }

}
addEvent(window,'load',showAdms);

