function addCnns() {
    var o = EWAC_WfUtil["WF"];
    var froms = [];
    var tos = [];
    for (var n in o.Cnns) {
        var c = o.Cnns[n];
        if (c.IsDelete) {
            continue;
        }
        froms.push(c.From);
        tos.push(c.To);
    }
    var ids = [];
    var names = [];
    var types = [];
    var xs = [];
    var ys = [];
    for (var n in o.Units) {
        var c = o.Units[n];
        var obj = $X(n);
        if (c.IsDelete) {
            continue;
        }
        ids.push(c.Id);
        names.push(GetInnerText(obj.getElementsByTagName('td')[1]).trim());
        types.push(c.Type);
        xs.push(obj.offsetLeft);
        ys.push(obj.offsetTop);


    }


    var ajax = new EWA_AjaxClass();

    var u = root + '?ref_id=@ref_id&wf_id=@wf_id&ewa_wf_type=all&configName=globaltravel';
    ajax.AddParameter('froms', froms.join(','));
    ajax.AddParameter('tos', tos.join(','));

    ajax.AddParameter('ids', ids.join(','));
    ajax.AddParameter('names', names.join(','));
    ajax.AddParameter('types', types.join(','));
    ajax.AddParameter('xs', xs.join(','));
    ajax.AddParameter('ys', ys.join(','));

    ajax.PostNew(u, function() {
        if (!ajax.IsRunEnd()) {
            return;
        }
        var rst
        eval('rst=' + ajax.GetRst());
        if (rst.RST) {
            alert('OK');
        } else {
            alert(rst.ERR);
        }

    });
}

