SELECT A.*,B.DLV_VER,C.DLV_NUMS FROM _EWA_WF A LEFT JOIN _EWA_WF_DLV B ON A.WF_ID=B.WF_ID AND B.WF_REF_ID=@REF_ID AND DLV_CUR='Y' LEFT JOIN ( SELECT WF_ID ID, COUNT(*) DLV_NUMS FROM _EWA_WF_DLV WHERE WF_REF_ID=@REF_ID GROUP BY WF_ID
) C ON A.WF_ID=C.ID
) AAAAAAAAAAAAAAA
WHERE 1=1 ORDER BY WF_ID

DELETE FROM _EWA_WF WHERE WF_ID = @WF_ID AND NOT EXISTS(
  SELECT * FROM _EWA_WF_DLV WHERE WF_ID=@WF_ID

)
