function clearSelect() {
    var o = window.frames[0].document.getElementsByTagName('input');
    for (var i = 0; i < o.length; i++) {
        var o1 = o[i];
        if (o1.checked) {
            o1.checked = false;
        }
    }
}

function addTables() {
    var os = window.frames[0].document.getElementsByTagName('input');
    var s = [];
    var r = /\;/ig;
    for (var i = 0; i < os.length; i++) {
        var o = os[i];
        if (o.type == 'checkbox' && o.checked) {
            s.push(o.value.replace(r, ',') + ",true,1=2");
        }
    }
    if (s.length == 0) {
        alert('请选择');
        return;
    }
    var s1 = s.join(';');
    var p = [];
    p[0] = {
        Name: 'tables',
        Value: s1
    };
    alert(s1);
    EWA.F.FOS['@SYS_FRAME_UNID'].DoAction(this, 'OnPagePost', '', '', p);

    //_EWA_DialogWnd._OpenerWindow.addTables1(s1);
    //_EWA_DialogWnd.CloseWindow();
}

function clearSelect() {
    var o = window.frames[0].frames[1].document.getElementsByTagName('input');
    for (var i = 0; i < o.length; i++) {
        var o1 = o[i];
        if (o1.checked) {
            o1.checked = false;
        }
    }
}

function addTables() {
    var os = window.frames[0].frames[1].document.getElementsByTagName('input');
    var s = [];
    for (var i = 0; i < os.length; i++) {
        var o = os[i];
        if (o.type == 'checkbox' && o.checked) {
            var tr = o.parentNode.parentNode.parentNode.parentNode;
            var ss = window.frames[0].frames[1].VIEW_XMLNAME + ',' +
                o.value + ',' + GetInnerText(tr.cells[4]).trim() + ',' + GetInnerText(tr.cells[3]).trim();
            s.push(ss);
        }
    }
    if (s.length == 0) {
        alert('请选择');
        return;
    }
    var s1 = s.join(';');
    //	alert(s1);
    var p = [];
    p[0] = {
        Name: 'items',
        Value: s1
    };
    EWA.F.FOS['@SYS_FRAME_UNID'].DoAction(this, 'OnPagePost', '', '', p);
}

