function createSQL(){
	var os=window.frames[0].frames[0].document.getElementsByTagName('input');
	var s=[];
	for(var i=0;i<os.length;i++){
		var o=os[i];
		if(o.type=='checkbox' && o.checked){
			s.push(o.value); 			
		} 		
	} 		
	if(s.length==0){
		alert('请选择'); 
		return;
	}
	var s1=s.join('|'); 
	document.forms[1].q.value=s1;
	document.forms[1].submit();
	//var u='../../EWA_DEFINE/cgi-bin/group/?q='+s1;
	//window.frames[0].frames[1].location.href=u;
}

function addReses(){
	var r=/\;/ig;
	var tb=$X('res');                     
	var s=[];                     
	for(var i=1;i<tb.rows.length;i++){
		var tr=tb.rows[i];
		if(tr.cells[0].childNodes[0].checked){
			var f=tr.cells[3].innerHTML;
			var f1=f.split('/');
			var f2=f1[f1.length-1];
			var s1=f2.replace(r,';;')+','+tr.cells[1].innerHTML.replace(r,';;')
				+","+tr.cells[3].innerHTML.replace(r,';;')
				+","+tr.cells[4].innerHTML.replace(r,';;');                      
			s.push(s1);       
		}                     
 	}       
	if(s.length==0){
		alert('no selected');       
		return;   
	}   
	var s2=s.join(';'); 
	var p=[];
	alert(s2);
	p[0]={Name:'reses',Value:s2}; 
	EWA.F.FOS['@SYS_FRAME_UNID'].DoAction(this, 'OnPagePost', '', '',p);          
}

Set.Bottom:
document.body.onload=function(){           
	EWA.CP="@sys_contextpath";           
	EWA.LANG="zhcn";        
	window.setTimeout(loadRes,1);           
	document.body.style.overflow='hidden';            
}        
function loadRes(){           
	for(var i=0;i<window.frames.length;i++){           
 		loadRes1(window.frames[i]);           
	}            
 for(var a in _res){           
  var tr = $X('res').insertRow(-1);           
  var td=tr.insertCell(-1);           
  td.innerHTML="<input type=checkbox checked>";     
    td.className='ewa_td_m';
  var td=tr.insertCell(-1);           
  td.innerHTML="图片";           
  td.className='ewa_td_m';           
  var td=tr.insertCell(-1);           
  td.innerHTML="<img src='" + a + "' width=16 height=16 onload='this.parentNode.parentNode.cells[3].innerHTML=this.src'>";           
  td.className='ewa_td_m';       
             
  var td=tr.insertCell(-1);           
  td.innerHTML=a;           
  td.className='ewa_td_m';       
  var td1=tr.insertCell(-1);           
  td1.innerHTML=_res[a];           
  td1.className='ewa_td_m';       
 }           
}           
var _res={};           
function loadRes1(win){           
 var imgs = win.document.getElementsByTagName('image');           
 for(var i=0;i<imgs.length;i++){           
  var src=imgs[i].src;           
  var s1=src.split(EWA.CP);           
  _res[EWA.CP+s1[1]]=win.location.search.replace('?','');       
 }           
 var divs=win.document.getElementsByTagName('div');           
 for(var i=0;i<divs.length;i++){           
  if(divs[i].style.backgroundImage !=""){           
   var src=divs[i].style.backgroundImage;           
   if(src.indexOf('url(')==0){           
    src=src.substring(4,src.length-1);           
    if(src.length>3){           
     src=EWA.CP+"/EWA_STYLE/cgi-bin/"+src           
     _res[src]=win.location.search.replace('?','');  
    }           
   }           
  }           
 }           
            
}

