function addTables(){
	EWA.UI.Dialog.OpenReloadClose("@SYS_FRAME_UNID", "|ewa|group1.xml", 
			"addTables", true, 'linkType=GROUP&EWA_FRAMESET_NO=1&ID=@ID');
}
function addTables1(s){
	var p=[];
	p[0]={Name:'tables', Value:s};
	EWA.F.FOS['@SYS_FRAME_UNID'].DoAction(this, 'OnAddTables','','',p);
	EWA.F.FOS['@SYS_FRAME_UNID'].Goto(1);
}
