function modify_cat_info() {
    var node = EWA.F.FOS["@SYS_FRAME_UNID"].GetFocusNode();
    var p = '_EWA_C_ID=' + node.Key;
    EWA.UI.Dialog.OpenReloadClear('@SYS_FRAME_UNID', '@xmlname', '_EWA_HOR_CAT.F.M', false, p);
}

function link(id) {
    var itemname = id == "ALL" ? "_EWA_HOR_SQL.LF.M" : "_EWA_HOR_FIELD.ListFrame.Modify"
    var url = EWA.CP + '/EWA_STYLE/cgi-bin/?XMLNAME=@xmlname&ITEMNAME=' + itemname;
    url += '&EWA_H_ID=@_EWA_H_ID&EWA_C_ID=' + id;
    window.parent.frames[1].location = url;
}

//_EWA_HOR_SQL.LF.M
function addNew__EWA_HOR_SQL() {
    var u1 = new EWA_UrlClass(EWA.F.FOS['@SYS_FRAME_UNID'].Url);
    var hid = u1.GetParameter("EWA_H_ID");
    u1.RemoveEwa();
    u1.AddParameter("EWA_H_ID", hid);
    u1.AddParameter("EWA_MTYPE", "N");

    var paras = u1.GetParas();
    EWA.UI.Dialog.OpenReloadClose('@SYS_FRAME_UNID', '@xmlName', '_EWA_HOR_SQL.F.NM', false, paras);
}

// 执行刷新后事件
(function () {
    var ewa = EWA.F.FOS['@sys_frame_unid'];
    ewa.ReloadAfter = function() {
        var ids_split = [];
        $('#EWA_LF_@SYS_FRAME_UNID .ewa-lf-data-row .ewa-col-EWA_F_IDS').each(function() {
            var ids = $(this).text();
            if (ids) {
                ids_split.push(ids)
            }
        });
        var u1 = new EWA_UrlClass(EWA.F.FOS["@SYS_FRAME_UNID"].Url);
        u1.AddParameter("ewa_action", "SAct0");
        u1.AddParameter("ewa_ajax", "json");

        var u = u1.GetUrl();
        var data = {
            ids_split: ids_split.join(",")
        };
        $JP(u, data, function(rst) {
            var map = $J2MAP(rst, 'EWA_F_ID');
            $('#EWA_LF_@SYS_FRAME_UNID .ewa-lf-data-row .ewa-col-EWA_F_IDS #EWA_F_IDS').each(function() {
                if($(this).text()==""){
                    return;
                }
                var ids = $(this).text().split(",");
                var ss = [];
                for (var n in ids) {
                    var id = ids[n];
                    var name = map[id]? map[id].EWA_F_DES: id;
                    ss.push(name);
                }
                $(this).text(ss.join(", "));
            });
        });
    };
    ewa.ReloadAfter();
})();

//提交后台数据时执行的检查或附加数据处理
function do_pos_before__EWA_HOR_SQL() {
    var ewa = EWA.F.FOS["@SYS_FRAME_UNID"];
    var EWA_F_IDS = [];
    for (var n in EWA.F.FOS["@SYS_FRAME_UNID"].initFields) {
        var f = EWA.F.FOS["@SYS_FRAME_UNID"].initFields[n];
        var field_id = f.EWA_F_ID;
        EWA_F_IDS.push(field_id);
    }
    if (EWA_F_IDS.length == 0) {
        $Tip('请选择字段');
        return false;
    }
    //附加数据处理
    var postAddData = {};
    postAddData.EWA_F_IDS = EWA_F_IDS.join(',');
    ewa.PostAddData = postAddData;


    return true;
}
function showUpdateSql(){
    create_sql_update();
}

function createView() {
    create_sql_select(function(sql, table, fields) { //在fields的脚本
        EWA.F.FOS["@SYS_FRAME_UNID"].initSQL = sql;
        EWA.F.FOS["@SYS_FRAME_UNID"].initTable = table;
        EWA.F.FOS["@SYS_FRAME_UNID"].initFields = fields;
        createViewSql();
    });
}

function createViewSql() {
    var name = $('#EWA_SQL_NAME').val();
    var view_sql = "CREATE VIEW V_" + name + " AS\n\n" + EWA.F.FOS["@SYS_FRAME_UNID"].initSQL;
    window.frames[0].editor.setValue(view_sql, 9);
}

function init__EWA_HOR_SQL_modify() {
    //提交后台数据时执行的检查或附加数据处理
    EWA.F.FOS["@SYS_FRAME_UNID"].DoPostBefore = do_pos_before__EWA_HOR_SQL;

    var u1 = new EWA_UrlClass(EWA.F.FOS["@SYS_FRAME_UNID"].Url);
    u1.AddParameter("itemname", "_EWA_HOR_FIELD.ListFrame.Modify");
    u1.AddParameter("CREATE_SQL", "1");
    u1.AddParameter("EWA_PAGESIZE", 500);
    $Install(u1.GetUrl(), "fields", function() {
        var ids = "@EWA_F_IDS".split(',');
        for (var n in ids) {
            $('#fields .IX_CHECKBOX[value="' + ids[n] + '"]').prop("checked", true);
        }
    });
}
addEvent(window, 'load', init__EWA_HOR_SQL_modify);

