function create_sql_select(callback) {
    var data = {
        ids_split: EWA.F.FOS["@SYS_FRAME_UNID"].SelectChecked()
    };
    var u1 = new EWA_UrlClass(EWA.F.FOS["@SYS_FRAME_UNID"].Url);
    u1.AddParameter("ewa_action", "ExtendAction0");
    u1.AddParameter("ewa_ajax", "json_all");

    var u = u1.GetUrl();
    $JP(u, data, function(rst) {
        var table = rst[0][0]; //表数据
        var fields = rst[1]; //字段数据
        EWAC_HorTableSql.createSqlSelect(table, fields, callback);
    });
}

