function move() {
    var ids = EWA.F.FOS["@SYS_FRAME_UNID"].SelectChecked();
    if (ids == '') {
        alert('not selected');
        return;
    }
    EWA.UI.Dialog.OpenReloadClose('@SYS_FRAME_UNID',
        '|ewa|data_hor.xml', '_EWA_HOR_CAT.Frame.Move', false,
        'EWA_H_ID=@EWA_H_ID&IDS_SPLIT=' + ids);
}


