function init__EWA_DEV_CNT_modify(){
	var X = "@xmlname";
	var I = "@itemname";
	var P = $U();
	var u_root = EWA.CP + "/EWA_STYLE/cgi-bin/?xmlname=" + X + "&itemname=" + I;
	if(P!=null){
		u_root=u_root+"&"+P;
	}
	var u_root_ajax=u_root+"&EWA_AJAX=json&EWA_ACTION=OnPagePost";
	// 执行初始化后事件
	$J(u_root_ajax,function(rst){
	    var ss=["<ol>"];
	    for(var n in rst){
	        var d=rst[n];
	        var fl=EWA_UI_BoxExt.getFileLen(d.DEV_ATT_SIZE);
	        var s="<li><a href='"+d.DEV_ATT_URL+"' target=_blank>"+d.DEV_ATT_DES+" ("+fl+")</a></li>";
	        ss.push(s);
	    }
	    ss.push('</ol>');
	    $('#atts').html(ss.join(''));

	});
}
addEvent(window,'load', init__EWA_DEV_CNT_modify);

