Set.Top:
function Show(obj){         
	var para={    
		Name:"ADM_DENY" ,    
		Value: obj.value    
	};    
	EWA.F.FOS["@SYS_FRAME_UNID"].GotoParas.length=0;      
	EWA.F.FOS["@SYS_FRAME_UNID"].GotoParas.push(para);      
	EWA.F.FOS["@SYS_FRAME_UNID"].Goto(1);        
}    
function AddNew(){                                        
	EWA.UI.Dialog.OpenReloadClose("@SYS_FRAME_UNID",                          
		"|ewa|ewa_admin.xml", "EWA_ADMIN.Frame.New", true, '');	                                       
}                  
function Delete(){                  
	var s1=EWA.F.FOS["@SYS_FRAME_UNID"].SelectChecked();                                 
	if(s1==''){                  
		alert('请先选择用户');                           
		return;                               
	}                 
	var paras=new Array();                       
	paras.push({Name:'IDS_SPLIT',Value:s1});                      
	EWA.F.FOS['@SYS_FRAME_UNID'].DoAction(this, 'OnFrameDelete', '你确认要删除么？', '', paras); 	                 
}            
function Deny(isdeny){            
	var s1=EWA.F.FOS["@SYS_FRAME_UNID"].SelectChecked();                                 
	if(s1==''){                              
		alert('请先选择用户');                           
		return;                               
	}                          
	var paras=new Array();                       
	paras.push({Name:'IDS_SPLIT',Value:s1});                      
	if(isdeny){                                 
		EWA.F.FOS['@SYS_FRAME_UNID'].DoAction(this, '禁用', '','', paras);                     
	}else{                                    
		EWA.F.FOS["@SYS_FRAME_UNID"].DoAction(this,'启用','','',paras);                     
	}                      
}

