Set.Top:
function NewFunc(){        
	EWA.UI.Dialog.OpenReloadClose("@SYS_FRAME_UNID", "|ewa|ewa_admin.xml", "EWA_FUNCTION.Frame.New", true, '');       
}       
function DenyFunc(isDeny){     
 	var s1=EWA.F.FOS["@SYS_FRAME_UNID"].SelectChecked();                                   
	if(s1==''){ 
		alert('请先选择');                             
		return;                     
	}                            
	var paras=new Array();  
	paras.push({Name:'IDS_SPLIT',Value:s1}); 
	var deny=  isDeny?'Y':'N';
	paras.push({Name:'DENY',Value:deny}); 
	EWA.F.FOS['@SYS_FRAME_UNID'].DoAction(this, '禁用', '','', paras);     
}

function AddToRelation(){
	var s1 = EWA.F.FOS["@SYS_FRAME_UNID"].SelectChecked();
	if(s1==''){
		var msg=EWA.F.FOS["@SYS_FRAME_UNID"].Resources["NoSelect"].GetInfo();
		alert(msg);
		return;
	}
	var paras=new Array();
	paras.push({
		Name: 'IDS_SPLIT',
		Value: s1
	});
	EWA.F.FOS['@SYS_FRAME_UNID'].DoAction(this, 'OnAddDataToRelation', '', '', paras);
}

Set.Sql:
SELECT * FROM EWA_FUNCTION WHERE NOT FUN_ID IN( 
	 SELECT FUN_ID FROM EWA_R_FT_FUNC B WHERE B.FT_ID =@FT_ID
)

Set.Top:
var type='@type';  
function ShowAdmins(obj){   
	var v=obj.childNodes[0].childNodes[0].value; 
	if(type=='FUNCTION'){

		var url="./?XMLNAME=|ewa|ewa_admin.xml&ITEMNAME=V_EWA_FUNC_GRP.ListFrame.ViewAndModify"     
	 		+"&GRP_ID="+v;
	}else{   
		var url="./?XMLNAME=|ewa|ewa_admin.xml&ITEMNAME=V_EWA_ADM_GRP.ListFrame.ViewAndModify"     
	 		+"&GRP_ID="+v;
	}     
	window.parent.frames[1].location=url;     
}

